import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:hive_flutter/hive_flutter.dart';
import 'package:timezone/data/latest.dart' as tz;
import 'package:timezone/timezone.dart' as tz;
import 'package:go_router/go_router.dart';
import 'package:flutter_local_notifications/flutter_local_notifications.dart';
import 'services/notification_service.dart';
import 'services/permission_service.dart';

void main() async {
  WidgetsFlutterBinding.ensureInitialized();

  // Initialize Hive
  await Hive.initFlutter();

  // Initialize timezone data
  tz.initializeTimeZones();

  // Properly detect and set the device's local timezone
  await _setCorrectTimezone();

  // Initialize notification service
  await NotificationService.initialize();

  // Request permissions
  await PermissionService.requestAllPermissions();

  runApp(const ProviderScope(child: MyApp()));
}

/// Detect and set the correct local timezone based on device settings
Future<void> _setCorrectTimezone() async {
  try {
    // Get the device's current timezone offset
    final now = DateTime.now();
    final deviceOffset = now.timeZoneOffset;

    print('Device timezone offset: ${deviceOffset.inHours} hours');
    print('Device local time: $now');

    // Try to find the correct timezone location based on offset
    // For Pacific Time (Seattle area), offset should be -7 or -8 hours
    String timezoneName;

    if (deviceOffset.inHours == -8) {
      timezoneName = 'America/Los_Angeles'; // PST (Pacific Standard Time)
    } else if (deviceOffset.inHours == -7) {
      timezoneName = 'America/Los_Angeles'; // PDT (Pacific Daylight Time)
    } else if (deviceOffset.inHours == -5) {
      timezoneName = 'America/New_York'; // EST/EDT
    } else if (deviceOffset.inHours == -6) {
      timezoneName = 'America/Chicago'; // CST/CDT
    } else if (deviceOffset.inHours == -7) {
      timezoneName = 'America/Denver'; // MST/MDT
    } else {
      // Default to Los Angeles for Seattle area
      timezoneName = 'America/Los_Angeles';
      print('Unknown offset ${deviceOffset.inHours}, defaulting to America/Los_Angeles');
    }

    // Set the timezone
    tz.setLocalLocation(tz.getLocation(timezoneName));

    final tzNow = tz.TZDateTime.now(tz.local);
    print('Timezone set to: ${tz.local.name}');
    print('TZ library time: $tzNow');
    print('Time difference: ${now.difference(tzNow.toLocal()).inMinutes} minutes');

  } catch (e) {
    print('Error setting timezone: $e');
    // Fallback to default Los Angeles timezone for Seattle area
    try {
      tz.setLocalLocation(tz.getLocation('America/Los_Angeles'));
      print('Fallback: Set timezone to America/Los_Angeles');
    } catch (fallbackError) {
      print('Fallback timezone setting failed: $fallbackError');
    }
  }
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp.router(
      title: 'HabitV8',
      theme: ThemeData(
        primarySwatch: Colors.blue,
        useMaterial3: true,
      ),
      routerConfig: _router,
    );
  }
}

