import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:permission_handler/permission_handler.dart';
import '../../services/permission_service.dart';
import '../../services/notification_service.dart';

class SettingsScreen extends ConsumerStatefulWidget {
  const SettingsScreen({super.key});

  @override
  ConsumerState<SettingsScreen> createState() => _SettingsScreenState();
}

class _SettingsScreenState extends ConsumerState<SettingsScreen> {
  ThemeMode _themeMode = ThemeMode.system;
  bool _notificationsEnabled = false;
  bool _calendarSync = false;
  bool _healthDataSync = false;
  Color _primaryColor = Colors.blue;
  bool _isLoading = true;

  final List<Color> _colorOptions = [
    Colors.blue,
    Colors.green,
    Colors.orange,
    Colors.purple,
    Colors.red,
    Colors.teal,
    Colors.indigo,
    Colors.pink,
    Colors.amber,
    Colors.cyan,
  ];

  @override
  void initState() {
    super.initState();
    _loadPermissionStatus();
  }

  /// Load current permission status when screen initializes
  Future<void> _loadPermissionStatus() async {
    try {
      final permissionService = PermissionService();

      final notificationStatus = await permissionService.isNotificationPermissionGranted();
      final calendarStatus = await permissionService.isCalendarPermissionGranted();
      final healthStatus = await permissionService.isHealthPermissionGranted();

      setState(() {
        _notificationsEnabled = notificationStatus;
        _calendarSync = calendarStatus;
        _healthDataSync = healthStatus;
        _isLoading = false;
      });
    } catch (e) {
      print('Error loading permission status: $e');
      setState(() {
        _isLoading = false;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    if (_isLoading) {
      return Scaffold(
        appBar: AppBar(
          title: const Text('Settings'),
        ),
        body: const Center(
          child: CircularProgressIndicator(),
        ),
      );
    }

    return Scaffold(
      appBar: AppBar(
        title: const Text('Settings'),
      ),
      body: ListView(
        padding: const EdgeInsets.all(16),
        children: [
          _SettingsSection(
            title: 'Appearance',
            children: [
              _SettingsTile(
                title: 'Theme',
                subtitle: _getThemeModeText(_themeMode),
                leading: const Icon(Icons.palette),
                onTap: () => _showThemeDialog(),
              ),
              _SettingsTile(
                title: 'Primary Color',
                subtitle: 'Customize your app\'s accent color',
                leading: CircleAvatar(
                  radius: 12,
                  backgroundColor: _primaryColor,
                ),
                onTap: () => _showColorPicker(),
              ),
            ],
          ),
          const SizedBox(height: 24),
          _SettingsSection(
            title: 'Notifications',
            children: [
              SwitchListTile(
                title: const Text('Enable Notifications'),
                subtitle: const Text('Get reminders for your habits'),
                value: _notificationsEnabled,
                onChanged: (value) => _toggleNotifications(value),
                secondary: const Icon(Icons.notifications),
              ),
              if (_notificationsEnabled)
                _SettingsTile(
                  title: 'Test Notifications',
                  subtitle: 'Send a test notification to verify it\'s working',
                  leading: const Icon(Icons.notification_add),
                  onTap: () => _testNotifications(),
                ),
            ],
          ),
          const SizedBox(height: 24),
          _SettingsSection(
            title: 'Integrations',
            children: [
              SwitchListTile(
                title: const Text('Calendar Sync'),
                subtitle: const Text('Sync habits with your calendar'),
                value: _calendarSync,
                onChanged: (value) => _toggleCalendarSync(value),
                secondary: const Icon(Icons.calendar_today),
              ),
              SwitchListTile(
                title: const Text('Health Data'),
                subtitle: const Text('Connect with health apps for insights'),
                value: _healthDataSync,
                onChanged: (value) => _toggleHealthDataSync(value),
                secondary: const Icon(Icons.favorite),
              ),
            ],
          ),
          const SizedBox(height: 24),
          _SettingsSection(
            title: 'Data & Privacy',
            children: [
              _SettingsTile(
                title: 'Export Data',
                subtitle: 'Download your habit data',
                leading: const Icon(Icons.download),
                onTap: () => _exportData(),
              ),
              _SettingsTile(
                title: 'Clear All Data',
                subtitle: 'Reset the app and delete all habits',
                leading: const Icon(Icons.delete_forever, color: Colors.red),
                onTap: () => _showClearDataDialog(),
                textColor: Colors.red,
              ),
            ],
          ),
          const SizedBox(height: 24),
          _SettingsSection(
            title: 'About',
            children: [
              _SettingsTile(
                title: 'Version',
                subtitle: '1.0.0',
                leading: const Icon(Icons.info),
                onTap: () => _showAboutDialog(),
              ),
              _SettingsTile(
                title: 'Privacy Policy',
                subtitle: 'View our privacy policy',
                leading: const Icon(Icons.privacy_tip),
                onTap: () => _showPrivacyPolicy(),
              ),
            ],
          ),
        ],
      ),
    );
  }

  String _getThemeModeText(ThemeMode mode) {
    switch (mode) {
      case ThemeMode.light:
        return 'Light';
      case ThemeMode.dark:
        return 'Dark';
      case ThemeMode.system:
        return 'System';
    }
  }

  void _showThemeDialog() {
    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: const Text('Choose Theme'),
        content: Column(
          mainAxisSize: MainAxisSize.min,
          children: [
            RadioListTile<ThemeMode>(
              title: const Text('Light'),
              value: ThemeMode.light,
              groupValue: _themeMode,
              onChanged: (value) {
                setState(() => _themeMode = value!);
                Navigator.of(context).pop();
              },
            ),
            RadioListTile<ThemeMode>(
              title: const Text('Dark'),
              value: ThemeMode.dark,
              groupValue: _themeMode,
              onChanged: (value) {
                setState(() => _themeMode = value!);
                Navigator.of(context).pop();
              },
            ),
            RadioListTile<ThemeMode>(
              title: const Text('System'),
              value: ThemeMode.system,
              groupValue: _themeMode,
              onChanged: (value) {
                setState(() => _themeMode = value!);
                Navigator.of(context).pop();
              },
            ),
          ],
        ),
      ),
    );
  }

  void _showColorPicker() {
    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: const Text('Choose Primary Color'),
        content: Wrap(
          spacing: 16,
          runSpacing: 16,
          children: _colorOptions.map((color) {
            return GestureDetector(
              onTap: () {
                setState(() => _primaryColor = color);
                Navigator.of(context).pop();
              },
              child: Container(
                width: 40,
                height: 40,
                decoration: BoxDecoration(
                  color: color,
                  shape: BoxShape.circle,
                  border: _primaryColor == color
                      ? Border.all(color: Colors.black, width: 3)
                      : null,
                ),
                child: _primaryColor == color
                    ? const Icon(Icons.check, color: Colors.white)
                    : null,
              ),
            );
          }).toList(),
        ),
      ),
    );
  }

  Future<void> _toggleNotifications(bool value) async {
    final permissionService = PermissionService();

    if (value) {
      final granted = await permissionService.requestPermission(Permission.notification);
      if (granted) {
        setState(() => _notificationsEnabled = true);
        _showSnackBar('Notifications enabled successfully! ');
      } else {
        setState(() => _notificationsEnabled = false);
        _showSnackBar('Permission denied. Please enable in device settings.');
      }
    } else {
      setState(() => _notificationsEnabled = false);
      _showSnackBar('Notifications disabled');
    }
  }

  Future<void> _toggleCalendarSync(bool value) async {
    final permissionService = PermissionService();

    if (value) {
      final granted = await permissionService.requestPermission(Permission.calendar);
      if (granted) {
        setState(() => _calendarSync = true);
        _showSnackBar('Calendar sync enabled successfully! ');
      } else {
        setState(() => _calendarSync = false);
        _showSnackBar('Calendar permission denied. Please enable in device settings.');
      }
    } else {
      setState(() => _calendarSync = false);
      _showSnackBar('Calendar sync disabled');
    }
  }

  Future<void> _toggleHealthDataSync(bool value) async {
    final permissionService = PermissionService();

    if (value) {
      try {
        final granted = await permissionService.isHealthPermissionGranted();
        if (!granted) {
          // Request health permissions through the permission service
          await permissionService.requestAllPermissions();
        }

        // Check again after request
        final finalStatus = await permissionService.isHealthPermissionGranted();
        setState(() => _healthDataSync = finalStatus);

        if (finalStatus) {
          _showSnackBar('Health data sync enabled successfully! ');
        } else {
          _showSnackBar('Health permission denied. Please enable in device settings.');
        }
      } catch (e) {
        setState(() => _healthDataSync = false);
        _showSnackBar('Error requesting health permission: $e');
      }
    } else {
      setState(() => _healthDataSync = false);
      _showSnackBar('Health data sync disabled');
    }
  }

  Future<void> _testNotifications() async {
    try {
      final notificationService = NotificationService();

      // Send a regular test notification
      await notificationService.showTestNotification();

      // Also show a demo achievement notification
      await Future.delayed(const Duration(seconds: 2));
      await notificationService.showAchievementNotification(
        id: 1000,
        achievementTitle: 'Settings Master!',
        description: 'You successfully tested the notification system! Your Android 16 live notifications are working perfectly.',
        badgeIcon: '@mipmap/ic_launcher',
        payload: 'settings_test',
      );

      _showSnackBar('Test notifications sent! Check your notification panel ');
    } catch (e) {
      _showSnackBar('Error sending test notification: $e');
    }
  }

  void _exportData() {
    // TODO: Implement data export functionality
    _showSnackBar('Data export feature coming soon!');
  }

  void _showClearDataDialog() {
    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: const Text('Clear All Data'),
        content: const Text(
          'This will permanently delete all your habits and progress. This action cannot be undone.',
        ),
        actions: [
          TextButton(
            onPressed: () => Navigator.of(context).pop(),
            child: const Text('Cancel'),
          ),
          TextButton(
            onPressed: () {
              Navigator.of(context).pop();
              _clearAllData();
            },
            style: TextButton.styleFrom(foregroundColor: Colors.red),
            child: const Text('Delete All'),
          ),
        ],
      ),
    );
  }

  void _clearAllData() {
    // TODO: Implement data clearing functionality
    _showSnackBar('All data cleared');
  }

  void _showAboutDialog() {
    showAboutDialog(
      context: context,
      applicationName: 'Habit Tracker',
      applicationVersion: '1.0.0',
      applicationIcon: const Icon(Icons.track_changes, size: 48),
      children: [
        const Text('A beautiful and intuitive habit tracking application built with Flutter.'),
        const SizedBox(height: 16),
        const Text('Features:'),
        const Text(' Visual habit tracking'),
        const Text(' Detailed analytics'),
        const Text(' Custom scheduling'),
        const Text(' Progress insights'),
      ],
    );
  }

  void _showPrivacyPolicy() {
    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: const Text('Privacy Policy'),
        content: const SingleChildScrollView(
          child: Text(
            'Your privacy is important to us. This app stores all data locally on your device. '
            'No personal information is collected or shared with third parties. '
            'Optional integrations (calendar, health data) require explicit permission and can be disabled at any time.',
          ),
        ),
        actions: [
          TextButton(
            onPressed: () => Navigator.of(context).pop(),
            child: const Text('Close'),
          ),
        ],
      ),
    );
  }

  void _showSnackBar(String message) {
    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(content: Text(message)),
    );
  }
}

class _SettingsSection extends StatelessWidget {
  final String title;
  final List<Widget> children;

  const _SettingsSection({
    required this.title,
