                return ListView.builder(
                  padding: const EdgeInsets.all(16),
                  itemCount: filteredHabits.length,
                  itemBuilder: (context, index) {
                    final habit = filteredHabits[index];
                    return _buildHabitCard(habit);
                  },
                );
              },
            ),
            loading: () => const Center(child: CircularProgressIndicator()),
            error: (error, stack) => Center(
              child: Text('Error: $error'),
            ),
          );
        },
      ),
    );
  }

  List<Habit> _filterHabits(List<Habit> habits) {
    List<Habit> filtered = habits;

    // Filter by category
    if (_selectedCategory != 'All') {
      filtered = filtered.where((habit) => habit.category == _selectedCategory).toList();
    }

    // Filter by time period
    switch (_selectedFilter) {
      case 'Today':
        filtered = filtered.where((habit) => _isHabitDueToday(habit)).toList();
        break;
      case 'This Week':
        filtered = filtered.where((habit) => _isHabitDueThisWeek(habit)).toList();
        break;
      case 'All':
      default:
        // No additional filtering needed
        break;
    }

    return filtered;
  }

  bool _isHabitDueToday(Habit habit) {
    final today = DateTime.now();
    return _isHabitDueOnDate(habit, today);
  }

  bool _isHabitDueThisWeek(Habit habit) {
    final now = DateTime.now();
    final startOfWeek = now.subtract(Duration(days: now.weekday - 1));
    final endOfWeek = startOfWeek.add(const Duration(days: 6));

    for (int i = 0; i <= 6; i++) {
      final day = startOfWeek.add(Duration(days: i));
      if (_isHabitDueOnDate(habit, day)) {
        return true;
      }
    }
    return false;
  }

  bool _isHabitDueOnDate(Habit habit, DateTime date) {
    switch (habit.frequency) {
      case HabitFrequency.hourly:
      case HabitFrequency.daily:
        return true;
      case HabitFrequency.weekly:
        final weekday = date.weekday;
        return habit.weeklySchedule.contains(weekday);
      case HabitFrequency.monthly:
        final day = date.day;
        return habit.monthlySchedule.contains(day);
      case HabitFrequency.yearly:
        final today = DateTime.now();
        return date.month == today.month && date.day == today.day;
    }
  }

  Widget _buildHabitCard(Habit habit) {
    final isCompletedToday = _isHabitCompletedToday(habit);
    final completionRate = _calculateCompletionRate(habit);
    final streakInfo = _getStreakInfo(habit);

    return Card(
      margin: const EdgeInsets.only(bottom: 12),
      child: InkWell(
        onTap: () => _showHabitDetails(habit),
        borderRadius: BorderRadius.circular(12),
