  }

  Widget _buildAchievementBadges(List<Habit> habits) {
    final badges = _generateBadges(habits);

    return Card(
      child: Padding(
        padding: const EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const Row(
              children: [
                Icon(Icons.emoji_events, color: Colors.orange),
                SizedBox(width: 8),
                Text(
                  'Achievement Badges',
                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
                ),
              ],
            ),
            const SizedBox(height: 16),
            if (badges.isEmpty)
              const Text(
                'Keep completing habits to earn your first badge! ',
                style: TextStyle(color: Colors.grey),
              )
            else
              Wrap(
                spacing: 12,
                runSpacing: 12,
                children: badges.map((badge) => _BadgeWidget(
                  icon: badge['icon'] as IconData,
                  label: badge['label'] as String,
                  color: badge['color'] as Color,
                  isEarned: badge['earned'] as bool,
                )).toList(),
              ),
