  Widget _buildRecommendations(List<Habit> habits) {
    final recommendations = _generateRecommendations(habits);

    return Card(
      child: Padding(
        padding: const EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const Row(
              children: [
                Icon(Icons.auto_awesome, color: Colors.blue),
                SizedBox(width: 8),
                Text(
                  'Smart Recommendations',
                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
                ),
              ],
            ),
            const SizedBox(height: 16),
            ...recommendations.map((rec) => Container(
              margin: const EdgeInsets.only(bottom: 12),
              padding: const EdgeInsets.all(12),
              decoration: BoxDecoration(
                color: Colors.blue.shade50,
                borderRadius: BorderRadius.circular(8),
                border: Border.all(color: Colors.blue.shade200),
              ),
              child: Row(
                children: [
                  Icon(rec['icon'] as IconData, color: Colors.blue),
                  const SizedBox(width: 12),
                  Expanded(
                    child: Text(
                      rec['text'] as String,
                      style: const TextStyle(fontSize: 14),
                    ),
                  ),
                ],
              ),
            )),
          ],
        ),
      ),
    );
  }

  Widget _buildTrendAnalysis(List<Habit> habits) {
    final analysis = _generateTrendAnalysis(habits);

    return Card(
      child: Padding(
        padding: const EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const Row(
              children: [
                Icon(Icons.trending_up, color: Colors.green),
                SizedBox(width: 8),
                Text(
                  'Trend Analysis',
                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
                ),
              ],
            ),
            const SizedBox(height: 16),
            ...analysis.map((trend) => Padding(
              padding: const EdgeInsets.only(bottom: 12),
              child: Row(
                children: [
                  Icon(
                    trend['trending'] as bool ? Icons.trending_up : Icons.trending_down,
                    color: trend['trending'] as bool ? Colors.green : Colors.orange,
                  ),
                  const SizedBox(width: 12),
                  Expanded(
                    child: Text(
                      trend['text'] as String,
                      style: const TextStyle(fontSize: 14),
                    ),
                  ),
                ],
              ),
            )),
          ],
        ),
      ),
    );
  }

  // AI-powered insight generation methods
  List<Map<String, dynamic>> _generateInsights(List<Habit> habits) {
    final insights = <Map<String, dynamic>>[];

    // Best performing habit insight
    if (habits.isNotEmpty) {
      final bestHabit = habits.reduce((a, b) =>
        a.completions.length > b.completions.length ? a : b);
      insights.add({
        'text': ' "${bestHabit.name}" is your star performer with ${bestHabit.completions.length} completions!',
        'color': Colors.green,
      });
    }

    // Consistency insight
    final consistentHabits = habits.where((habit) => _calculateStreakDays(habit) >= 3).length;
    if (consistentHabits > 0) {
      insights.add({
        'text': ' You\'re building consistency! $consistentHabits habits have a 3+ day streak.',
        'color': Colors.orange,
      });
    }

    // Category balance insight
    final categories = habits.map((h) => h.category).toSet();
    if (categories.length >= 3) {
      insights.add({
        'text': ' Great balance! You\'re working on ${categories.length} different life areas.',
        'color': Colors.blue,
      });
    }

    // Weekly pattern insight
    final weekendCompletions = _getWeekendCompletionRate(habits);
    if (weekendCompletions > 0.7) {
      insights.add({
        'text': ' You\'re excellent at maintaining habits on weekends!',
        'color': Colors.purple,
      });
    } else if (weekendCompletions < 0.3) {
      insights.add({
        'text': ' Consider setting easier weekend goals to maintain momentum.',
        'color': Colors.amber,
      });
    }

    return insights;
  }

  List<Map<String, dynamic>> _generateBadges(List<Habit> habits) {
    final badges = <Map<String, dynamic>>[];

    // First steps badge
    badges.add({
      'icon': Icons.directions_walk,
      'label': 'First Steps',
      'color': Colors.green,
      'earned': habits.any((h) => h.completions.isNotEmpty),
    });

    // Consistency badge
    badges.add({
      'icon': Icons.whatshot,
      'label': '7-Day Streak',
      'color': Colors.orange,
      'earned': habits.any((h) => _calculateStreakDays(h) >= 7),
    });

    // Variety badge
    badges.add({
      'icon': Icons.diversity_3,
      'label': 'Life Balance',
      'color': Colors.blue,
      'earned': habits.map((h) => h.category).toSet().length >= 3,
    });

    // Dedication badge
    badges.add({
      'icon': Icons.emoji_events,
      'label': 'Century Club',
      'color': Colors.purple,
      'earned': habits.fold<int>(0, (sum, h) => sum + h.completions.length) >= 100,
    });

    return badges;
  }

  List<Map<String, dynamic>> _generateRecommendations(List<Habit> habits) {
    final recommendations = <Map<String, dynamic>>[];

    if (habits.length < 3) {
      recommendations.add({
        'icon': Icons.add_circle,
        'text': 'Consider adding 1-2 more habits for a balanced routine.',
      });
    }

    final categories = habits.map((h) => h.category).toSet();
    if (!categories.contains('Health')) {
      recommendations.add({
        'icon': Icons.favorite,
        'text': 'Add a health-focused habit like drinking water or walking.',
      });
    }

    final lowPerformers = habits.where((h) =>
      h.completions.length < (DateTime.now().difference(h.createdAt).inDays * 0.3)).toList();
    if (lowPerformers.isNotEmpty) {
      recommendations.add({
        'icon': Icons.tune,
        'text': 'Consider simplifying "${lowPerformers.first.name}" to build momentum.',
      });
    }

    return recommendations;
  }

  List<Map<String, dynamic>> _generateTrendAnalysis(List<Habit> habits) {
    final analysis = <Map<String, dynamic>>[];

    for (final habit in habits.take(3)) {
      final recentRate = _getRecentCompletionRate(habit);
      final isImproving = recentRate > 0.5;

      analysis.add({
        'text': '${habit.name}: ${isImproving ? "Improving" : "Needs attention"} (${(recentRate * 100).toInt()}% this week)',
        'trending': isImproving,
      });
    }

    return analysis;
  }

  // Helper methods
  int _calculateStreakDays(Habit habit) {
    if (habit.completions.isEmpty) return 0;

    final sortedCompletions = habit.completions.toList()
      ..sort((a, b) => b.compareTo(a));

    int streak = 0;
    final today = DateTime.now();

    for (int i = 0; i < 30; i++) {
      final checkDate = today.subtract(Duration(days: i));
      final hasCompletion = sortedCompletions.any((completion) {
        final completionDate = DateTime(completion.year, completion.month, completion.day);
        final checkDateOnly = DateTime(checkDate.year, checkDate.month, checkDate.day);
        return completionDate == checkDateOnly;
      });

      if (hasCompletion) {
        streak++;
      } else if (i > 0) {
        break;
      }
    }

    return streak;
  }

  double _getWeekendCompletionRate(List<Habit> habits) {
    int weekendCompletions = 0;
    int totalWeekends = 0;

    for (final habit in habits) {
      for (final completion in habit.completions) {
        if (completion.weekday == DateTime.saturday || completion.weekday == DateTime.sunday) {
          weekendCompletions++;
        }
      }

      final weeksSinceCreation = DateTime.now().difference(habit.createdAt).inDays ~/ 7;
      totalWeekends += weeksSinceCreation * 2;
    }

    return totalWeekends > 0 ? weekendCompletions / totalWeekends : 0.0;
  }

  double _getRecentCompletionRate(Habit habit) {
    final lastWeek = DateTime.now().subtract(const Duration(days: 7));
    final recentCompletions = habit.completions.where((c) => c.isAfter(lastWeek)).length;
    return recentCompletions / 7.0;
  }
}

class _BadgeWidget extends StatelessWidget {
  final IconData icon;
  final String label;
  final Color color;
  final bool isEarned;

  const _BadgeWidget({
    required this.icon,
    required this.label,
    required this.color,
    required this.isEarned,
  });

  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        Container(
          width: 60,
          height: 60,
          decoration: BoxDecoration(
            color: isEarned ? color : Colors.grey.shade300,
            shape: BoxShape.circle,
            boxShadow: isEarned ? [
              BoxShadow(
                color: color.withOpacity(0.3),
                blurRadius: 8,
                spreadRadius: 2,
              ),
            ] : null,
          ),
          child: Icon(
            icon,
            color: isEarned ? Colors.white : Colors.grey.shade600,
            size: 28,
          ),
        ),
        const SizedBox(height: 8),
        Text(
          label,
          style: TextStyle(
            fontSize: 12,
            fontWeight: FontWeight.w500,
            color: isEarned ? Colors.black87 : Colors.grey.shade600,
          ),
          textAlign: TextAlign.center,
        ),
      ],
    );
  }
}

