import 'package:device_calendar/device_calendar.dart';
import 'dart:io';
import 'logging_service.dart';

class CalendarService {
  static DeviceCalendarPlugin? _deviceCalendarPlugin;
  static bool _isInitialized = false;
  static List<Calendar> _calendars = [];

  /// Initialize calendar service
  static Future<bool> initialize() async {
    if (_isInitialized) return true;

    try {
      _deviceCalendarPlugin = DeviceCalendarPlugin();
      _isInitialized = true;
      AppLogger.info('Calendar service initialized');
      return true;
    } catch (e) {
      AppLogger.error('Failed to initialize calendar service', e);
      return false;
    }
  }

  /// Request calendar permissions
  static Future<bool> requestPermissions() async {
    if (!_isInitialized) {
      await initialize();
    }

    try {
      var permissionsGranted = await _deviceCalendarPlugin!.requestPermissions();

      if (permissionsGranted.isSuccess && permissionsGranted.data == true) {
        AppLogger.info('Calendar permissions granted');
        await _loadCalendars();
        return true;
      } else {
        AppLogger.error('Calendar permissions denied');
        return false;
      }
    } catch (e) {
      AppLogger.error('Failed to request calendar permissions', e);
      return false;
    }
  }

  /// Check if calendar permissions are granted
  static Future<bool> hasPermissions() async {
    if (!_isInitialized) {
      await initialize();
    }

    try {
      var permissionsGranted = await _deviceCalendarPlugin!.hasPermissions();
      return permissionsGranted.isSuccess && permissionsGranted.data == true;
    } catch (e) {
      AppLogger.error('Error checking calendar permissions', e);
      return false;
    }
  }

  /// Load available calendars
  static Future<void> _loadCalendars() async {
    try {
      var calendarsResult = await _deviceCalendarPlugin!.retrieveCalendars();

      if (calendarsResult.isSuccess && calendarsResult.data != null) {
        _calendars = calendarsResult.data!;
        AppLogger.info('Loaded ${_calendars.length} calendars');
      }
    } catch (e) {
      AppLogger.error('Failed to load calendars', e);
    }
  }

  /// Get list of available calendars
  static Future<List<Calendar>> getCalendars() async {
    if (!_isInitialized) {
      await initialize();
    }

    if (_calendars.isEmpty) {
      await _loadCalendars();
    }

    return _calendars;
  }

  /// Get the primary calendar
  static Future<Calendar?> getPrimaryCalendar() async {
    final calendars = await getCalendars();

    // Try to find the primary calendar
    Calendar? primary = calendars.where((cal) => cal.isDefault == true).firstOrNull;

    // If no primary found, use the first writable calendar
    primary ??= calendars.where((cal) => cal.isReadOnly == false).firstOrNull;

    // If still none found, use the first calendar
    primary ??= calendars.isNotEmpty ? calendars.first : null;

    AppLogger.info('Primary calendar: ${primary?.name}');
    return primary;
  }

  /// Create a habit event in the calendar
  static Future<bool> createHabitEvent({
    required String habitName,
    required DateTime scheduledTime,
    required Duration duration,
    String? description,
    String? calendarId,
  }) async {
    if (!_isInitialized || !await hasPermissions()) {
      AppLogger.error('Calendar not initialized or no permissions');
      return false;
    }

    try {
      // Get calendar to use
      Calendar? calendar;
      if (calendarId != null) {
        calendar = _calendars.where((cal) => cal.id == calendarId).firstOrNull;
      }
      calendar ??= await getPrimaryCalendar();

      if (calendar == null) {
        AppLogger.error('No calendar available');
        return false;
      }

      // Create the event
      final event = Event(
        calendar.id,
        title: ' $habitName',
        description: description ?? 'Habit reminder for $habitName',
        start: TZDateTime.from(scheduledTime, TimeZone.getDefault()),
        end: TZDateTime.from(scheduledTime.add(duration), TimeZone.getDefault()),
        allDay: false,
        availability: Availability.Busy,
        status: EventStatus.Confirmed,
      );

      final createResult = await _deviceCalendarPlugin!.createOrUpdateEvent(event);

      if (createResult?.isSuccess == true) {
        AppLogger.info('Created habit event: $habitName');
        return true;
      } else {
        AppLogger.error('Failed to create habit event: ${createResult?.errorMessages}');
        return false;
      }
    } catch (e) {
      AppLogger.error('Failed to create habit event', e);
      return false;
    }
  }

  /// Create recurring habit events for a week
  static Future<bool> createWeeklyHabitEvents({
    required String habitName,
    required List<int> weekdays, // 1-7, where 1 is Monday
    required TimeOfDay time,
    required Duration duration,
    String? description,
    String? calendarId,
  }) async {
    try {
      final now = DateTime.now();
      final startOfWeek = now.subtract(Duration(days: now.weekday - 1));

      bool allSuccess = true;

      for (int weekday in weekdays) {
        final eventDate = startOfWeek.add(Duration(days: weekday - 1));
        final scheduledTime = DateTime(
          eventDate.year,
          eventDate.month,
          eventDate.day,
          time.hour,
          time.minute,
        );

        // Only create events for future dates
        if (scheduledTime.isAfter(now)) {
          final success = await createHabitEvent(
            habitName: habitName,
            scheduledTime: scheduledTime,
            duration: duration,
            description: description,
            calendarId: calendarId,
          );

          if (!success) {
            allSuccess = false;
          }
        }
      }

      return allSuccess;
    } catch (e) {
      AppLogger.error('Failed to create weekly habit events', e);
      return false;
    }
  }

  /// Get habit events for a date range
  static Future<List<Event>> getHabitEvents({
    required DateTime startDate,
    required DateTime endDate,
    String? calendarId,
  }) async {
    if (!_isInitialized || !await hasPermissions()) {
      return [];
    }

    try {
      Calendar? calendar;
      if (calendarId != null) {
        calendar = _calendars.where((cal) => cal.id == calendarId).firstOrNull;
      }
      calendar ??= await getPrimaryCalendar();

      if (calendar == null) {
        return [];
      }

      final eventsResult = await _deviceCalendarPlugin!.retrieveEvents(
        calendar.id!,
        RetrieveEventsParams(
          startDate: startDate,
          endDate: endDate,
        ),
      );

      if (eventsResult.isSuccess && eventsResult.data != null) {
        // Filter for habit events (those starting with )
        final habitEvents = eventsResult.data!
            .where((event) => event.title?.startsWith('') == true)
            .toList();

        AppLogger.info('Retrieved ${habitEvents.length} habit events');
        return habitEvents;
      }
    } catch (e) {
      AppLogger.error('Failed to get habit events', e);
    }

    return [];
  }

  /// Delete a habit event
  static Future<bool> deleteHabitEvent(String eventId, String calendarId) async {
    if (!_isInitialized || !await hasPermissions()) {
      return false;
    }

    try {
      final deleteResult = await _deviceCalendarPlugin!.deleteEvent(calendarId, eventId);

      if (deleteResult?.isSuccess == true) {
        AppLogger.info('Deleted habit event: $eventId');
        return true;
      } else {
        AppLogger.error('Failed to delete habit event: ${deleteResult?.errorMessages}');
        return false;
      }
    } catch (e) {
      AppLogger.error('Failed to delete habit event', e);
      return false;
    }
  }

  /// Sync habit completion with calendar (mark as completed)
  static Future<bool> markHabitEventCompleted({
    required String eventId,
    required String calendarId,
    required DateTime completedTime,
  }) async {
    if (!_isInitialized || !await hasPermissions()) {
      return false;
    }

    try {
      // Get the existing event
      final eventsResult = await _deviceCalendarPlugin!.retrieveEvents(
        calendarId,
        RetrieveEventsParams(
          startDate: completedTime.subtract(const Duration(days: 1)),
          endDate: completedTime.add(const Duration(days: 1)),
        ),
      );

      if (eventsResult.isSuccess && eventsResult.data != null) {
        final event = eventsResult.data!
            .where((e) => e.eventId == eventId)
            .firstOrNull;

        if (event != null) {
          // Update the event title to show completion
          final updatedEvent = Event(
            event.calendarId,
            eventId: event.eventId,
            title: ' ${event.title?.replaceFirst('', '') ?? ''}',
            description: '${event.description ?? ''}\n\nCompleted at: ${completedTime.toString()}',
            start: event.start,
            end: event.end,
            allDay: event.allDay,
            availability: Availability.Free,
            status: EventStatus.Confirmed,
          );

          final updateResult = await _deviceCalendarPlugin!.createOrUpdateEvent(updatedEvent);

          if (updateResult?.isSuccess == true) {
            AppLogger.info('Marked habit event as completed: $eventId');
            return true;
          }
        }
      }
    } catch (e) {
      AppLogger.error('Failed to mark habit event as completed', e);
    }

    return false;
  }

  /// Get upcoming habit events for today
  static Future<List<Event>> getTodayUpcomingHabits() async {
    final now = DateTime.now();
    final startOfDay = DateTime(now.year, now.month, now.day);
    final endOfDay = startOfDay.add(const Duration(days: 1));

    final events = await getHabitEvents(
      startDate: startOfDay,
      endDate: endOfDay,
    );

    // Filter for upcoming events (not completed)
    final upcomingEvents = events
        .where((event) => 
          event.start?.isAfter(now) == true && 
          !event.title!.startsWith(''))
        .toList();

    // Sort by start time
    upcomingEvents.sort((a, b) => a.start!.compareTo(b.start!));

    AppLogger.info('Found ${upcomingEvents.length} upcoming habit events today');
    return upcomingEvents;
  }

  /// Get habit completion stats from calendar
  static Future<Map<String, dynamic>> getHabitStatsFromCalendar({
    required DateTime startDate,
    required DateTime endDate,
  }) async {
    final events = await getHabitEvents(
      startDate: startDate,
      endDate: endDate,
    );

    final Map<String, dynamic> stats = {
      'totalHabits': events.length,
      'completedHabits': 0,
      'completionRate': 0.0,
      'habitBreakdown': <String, Map<String, int>>{},
    };

    final Map<String, Map<String, int>> habitBreakdown = {};

    for (final event in events) {
      final habitName = event.title?.replaceAll(RegExp(r'[]\s*'), '') ?? 'Unknown';
      final isCompleted = event.title?.startsWith('') == true;

      habitBreakdown[habitName] ??= {'total': 0, 'completed': 0};
      habitBreakdown[habitName]!['total'] = habitBreakdown[habitName]!['total']! + 1;

      if (isCompleted) {
        stats['completedHabits'] = (stats['completedHabits'] as int) + 1;
        habitBreakdown[habitName]!['completed'] = habitBreakdown[habitName]!['completed']! + 1;
      }
    }

    if (events.isNotEmpty) {
      stats['completionRate'] = (stats['completedHabits'] as int) / events.length;
    }

    stats['habitBreakdown'] = habitBreakdown;

    AppLogger.info('Calendar habit stats: $stats');
    return stats;
  }
}

class TimeOfDay {
  final int hour;
  final int minute;

  const TimeOfDay({required this.hour, required this.minute});

  @override
  String toString() => '${hour.toString().padLeft(2, '0')}:${minute.toString().padLeft(2, '0')}';
}

