import 'package:hive/hive.dart';

part 'habit.g.dart';

@HiveType(typeId: 0)
class Habit extends HiveObject {
  @HiveField(0)
  late String id;

  @HiveField(1)
  late String name;

  @HiveField(2)
  String? description;

  @HiveField(3)
  late String category;

  @HiveField(4)
  late int colorValue;

  @HiveField(5)
  late DateTime createdAt;

  @HiveField(6)
  DateTime? nextDueDate;

  @HiveField(7)
  late HabitFrequency frequency;

  @HiveField(8)
  late int targetCount;

  @HiveField(9)
  List<DateTime> completions = [];

  @HiveField(10)
  int currentStreak = 0;

  @HiveField(11)
  int longestStreak = 0;

  @HiveField(12)
  bool isActive = true;

  @HiveField(13)
  bool notificationsEnabled = true;

  @HiveField(14)
  DateTime? notificationTime;

  @HiveField(15)
  List<int> weeklySchedule = [];

  @HiveField(16)
  List<int> monthlySchedule = [];

  @HiveField(17)
  DateTime? reminderTime;

  @HiveField(18)
  HabitDifficulty difficulty = HabitDifficulty.medium;

  Habit();

  Habit.create({
    required this.name,
    this.description,
    required this.category,
    required this.colorValue,
    required this.frequency,
    this.targetCount = 1,
    this.notificationsEnabled = true,
    this.notificationTime,
    this.reminderTime,
    this.weeklySchedule = const [],
    this.monthlySchedule = const [],
    this.difficulty = HabitDifficulty.medium,
  }) {
    id = DateTime.now().millisecondsSinceEpoch.toString();
    createdAt = DateTime.now();
    isActive = true;
    currentStreak = 0;
    longestStreak = 0;
    completions = [];
  }

  // Calculate completion rate based on the last 30 days
  double get completionRate {
    if (completions.isEmpty) return 0.0;

    final now = DateTime.now();
    final thirtyDaysAgo = now.subtract(const Duration(days: 30));

    final recentCompletions = completions.where((completion) =>
        completion.isAfter(thirtyDaysAgo) &&
        completion.isBefore(now.add(const Duration(days: 1)))).length;

    // Expected completions based on frequency
    int expectedCompletions;
    switch (frequency) {
      case HabitFrequency.daily:
        expectedCompletions = 30;
        break;
      case HabitFrequency.weekly:
        expectedCompletions = 4;
        break;
      case HabitFrequency.monthly:
        expectedCompletions = 1;
        break;
      default:
        expectedCompletions = 30; // Default to daily
    }

    return (recentCompletions / expectedCompletions).clamp(0.0, 1.0);
  }
}

@HiveType(typeId: 1)
enum HabitFrequency {
  @HiveField(0)
  hourly,
  @HiveField(1)
  daily,
  @HiveField(2)
  weekly,
  @HiveField(3)
  monthly,
  @HiveField(4)
  yearly,
}

@HiveType(typeId: 2)
enum HabitDifficulty {
  @HiveField(0)
  easy,
  @HiveField(1)
  medium,
  @HiveField(2)
  hard,
}

