Future<void> _setCorrectTimezone() async {
  try {
    // Use a more robust approach to get the system timezone
    final String? timezoneName = await _getSystemTimezone();

    if (timezoneName != null) {
      tz.setLocalLocation(tz.getLocation(timezoneName));
    } else {
      // Fallback to a better default detection
      final now = DateTime.now();
      final deviceOffset = now.timeZoneOffset;

      // Use a map for better timezone detection
      final timezoneMap = <int, String>{
        -12: 'Pacific/Wake',
        -11: 'Pacific/Midway',
        -10: 'Pacific/Honolulu',
        -9: 'America/Anchorage',
        -8: 'America/Los_Angeles',
        -7: 'America/Denver',
        -6: 'America/Chicago',
        -5: 'America/New_York',
        -4: 'America/Halifax',
        -3: 'America/Sao_Paulo',
        0: 'Europe/London',
        1: 'Europe/Paris',
        2: 'Europe/Berlin',
        3: 'Europe/Moscow',
        8: 'Asia/Shanghai',
        9: 'Asia/Tokyo',
      };

      final timezoneName = timezoneMap[deviceOffset.inHours] ?? 'America/Los_Angeles';
      tz.setLocalLocation(tz.getLocation(timezoneName));
    }
  } catch (e) {
    AppLogger.error('Error setting timezone', e);
    try {
      tz.setLocalLocation(tz.getLocation('America/Los_Angeles'));
    } catch (fallbackError) {
      AppLogger.error('Fallback timezone setting failed', fallbackError);
    }
  }
}

