  Future<void> _saveHabit() async {
    if (!_formKey.currentState!.validate()) {
      return;
    }

    // Validate frequency-specific requirements
    if (_selectedFrequency == HabitFrequency.weekly && _selectedWeekdays.isEmpty) {
      ScaffoldMessenger.of(context).showSnackBar(
        const SnackBar(
          content: Text('Please select at least one day for weekly habits'),
        ),
      );
      return;
    }

    if (_selectedFrequency == HabitFrequency.monthly && _selectedMonthDays.isEmpty) {
      ScaffoldMessenger.of(context).showSnackBar(
        const SnackBar(
          content: Text('Please select at least one day for monthly habits'),
        ),
      );
      return;
    }

    try {
      final habitServiceAsync = ref.read(habitServiceProvider);

      habitServiceAsync.when(
        data: (habitService) async {
          final habit = Habit.create(
            name: _nameController.text.trim(),
            description: _descriptionController.text.trim().isEmpty
                ? null
                : _descriptionController.text.trim(),
            category: _selectedCategory,
            colorValue: _selectedColor.toARGB32(),
            frequency: _selectedFrequency,
            targetCount: _targetCount,
            notificationsEnabled: _notificationsEnabled,
            notificationTime: _notificationTime != null
                ? DateTime(2024, 1, 1, _notificationTime!.hour, _notificationTime!.minute)
                : null,
            weeklySchedule: _selectedWeekdays,
            monthlySchedule: _selectedMonthDays,
          );

          await habitService.addHabit(habit);

          // Schedule notifications if enabled
          if (_notificationsEnabled && _notificationTime != null) {
            await _scheduleHabitNotifications(habit);
          }

          if (mounted) {
            ScaffoldMessenger.of(context).showSnackBar(
              SnackBar(
                content: Text(_notificationsEnabled && _notificationTime != null
                    ? 'Habit created with notifications scheduled!'
                    : 'Habit created successfully!'),
                backgroundColor: Colors.green,
              ),
            );
            context.pop();
          }
        },
        loading: () {
          // Show loading indicator
          if (mounted) {
            ScaffoldMessenger.of(context).showSnackBar(
              const SnackBar(content: Text('Loading...')),
            );
          }
        },
        error: (error, stack) {
          if (mounted) {
            ScaffoldMessenger.of(context).showSnackBar(
              SnackBar(
                content: Text('Error creating habit: $error'),
                backgroundColor: Colors.red,
              ),
            );
          }
        },
      );
    } catch (e) {
      if (mounted) {
        ScaffoldMessenger.of(context).showSnackBar(
          SnackBar(
            content: Text('Error creating habit: $e'),
            backgroundColor: Colors.red,
          ),
        );
      }
    }
  }

  /// Schedule notifications for the created habit based on its frequency
  Future<void> _scheduleHabitNotifications(Habit habit) async {
    if (!habit.notificationsEnabled || habit.notificationTime == null) {
      return;
    }

    final notificationTime = habit.notificationTime!;
    final hour = notificationTime.hour;
    final minute = notificationTime.minute;

    try {
      switch (habit.frequency) {
        case HabitFrequency.daily:
          // Schedule daily recurring notification
          await NotificationService.scheduleDailyHabitReminder(
            habitId: habit.id,
            habitName: habit.name,
            reminderTime: notificationTime,
          );
          AppLogger.info('Scheduled daily notification for "${habit.name}" at $hour:$minute');
          break;

        case HabitFrequency.weekly:
          // Schedule notifications for selected weekdays
          for (int weekday in habit.weeklySchedule) {
            final now = DateTime.now();
            DateTime nextNotification = DateTime(now.year, now.month, now.day, hour, minute);

            // Find the next occurrence of this weekday
            while (nextNotification.weekday != weekday) {
              nextNotification = nextNotification.add(const Duration(days: 1));
            }

            // If the time has passed today, schedule for next week
            if (nextNotification.isBefore(now)) {
              nextNotification = nextNotification.add(const Duration(days: 7));
            }

            await NotificationService.scheduleNotification(
              id: habit.hashCode + weekday, // Unique ID for each weekday
              title: ' ${habit.name}',
              body: 'Time to complete your habit! Don\'t break your streak.',
              scheduledTime: nextNotification,
              payload: 'habit_${habit.hashCode}_weekday_$weekday',
            );
            AppLogger.info('Scheduled weekly notification for "${habit.name}" on weekday $weekday at ${nextNotification.toString()}');
          }
          break;

        case HabitFrequency.monthly:
          // Schedule notifications for selected month days
          for (int monthDay in habit.monthlySchedule) {
            final now = DateTime.now();
            DateTime nextNotification = DateTime(now.year, now.month, monthDay, hour, minute);

            // If the date has passed this month, schedule for next month
            if (nextNotification.isBefore(now)) {
              nextNotification = DateTime(now.year, now.month + 1, monthDay, hour, minute);
            }

            await NotificationService.scheduleNotification(
              id: habit.hashCode + monthDay + 1000, // Unique ID for each month day
              title: ' ${habit.name}',
              body: 'Monthly habit reminder - keep up the great work!',
              scheduledTime: nextNotification,
              payload: 'habit_${habit.hashCode}_monthday_$monthDay',
            );
            AppLogger.info('Scheduled monthly notification for "${habit.name}" on day $monthDay at ${nextNotification.toString()}');
          }
          break;

        case HabitFrequency.hourly:
          // For hourly habits, schedule next few occurrences
          final now = DateTime.now();
          for (int i = 1; i <= 24; i++) { // Schedule next 24 hours
            final nextNotification = DateTime(now.year, now.month, now.day, now.hour + i, minute);

            await NotificationService.scheduleNotification(
              id: habit.hashCode + i + 2000, // Unique ID for each hourly notification
              title: ' ${habit.name}',
              body: 'Hourly reminder - time for your habit!',
              scheduledTime: nextNotification,
              payload: 'habit_${habit.hashCode}_hourly_$i',
            );
          }
          AppLogger.info('Scheduled 24 hourly notifications for "${habit.name}"');
          break;

        case HabitFrequency.yearly:
          // Schedule for next year at the same time
          final now = DateTime.now();
          final nextNotification = DateTime(now.year + 1, now.month, now.day, hour, minute);

          await NotificationService.scheduleNotification(
            id: habit.hashCode + 3000, // Unique ID for yearly notification
            title: ' ${habit.name}',
            body: 'Your yearly habit is due - make it count!',
            scheduledTime: nextNotification,
            payload: 'habit_${habit.hashCode}_yearly',
          );
          AppLogger.info('Scheduled yearly notification for "${habit.name}" at ${nextNotification.toString()}');
          break;
      }

    } catch (e) {
      AppLogger.error('Error scheduling notifications for habit "${habit.name}"', e);
      if (mounted) {
        ScaffoldMessenger.of(context).showSnackBar(
          SnackBar(
            content: Text('Habit created but notifications failed to schedule: $e'),
            backgroundColor: Colors.orange,
          ),
        );
      }
    }
  }
}

